import React, { useEffect, useState } from 'react';
import { ping, runPhishing, runLateral, getTimeline, login, register, logout, getAIEvasion } from './api';

const cardStyle = {
  background: '#fff',
  borderRadius: 8,
  boxShadow: '0 2px 8px rgba(0,0,0,0.07)',
  padding: 24,
  margin: '20px 0',
  maxWidth: 700,
};

const buttonStyle = {
  background: '#1976d2',
  color: '#fff',
  border: 'none',
  borderRadius: 4,
  padding: '10px 20px',
  margin: '0 8px 0 0',
  cursor: 'pointer',
  fontWeight: 600,
  fontSize: 16,
  transition: 'background 0.2s',
};

const buttonAltStyle = {
  ...buttonStyle,
  background: '#43a047',
};

const logoutButtonStyle = {
  ...buttonStyle,
  background: '#d32f2f',
  float: 'right',
};

function App() {
  const [backendMsg, setBackendMsg] = useState('');
  const [attackResult, setAttackResult] = useState(null);
  const [timeline, setTimeline] = useState([]);
  const [aiEvasion, setAIEvasion] = useState(1);
  const [auth, setAuth] = useState(false);
  const [authMode, setAuthMode] = useState('login');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    ping().then(data => setBackendMsg(data.message));
    fetchTimeline();
    fetchAIEvasion();
    // Try to auto-login (if session exists)
    // Not implemented: would require a /me endpoint
  }, [auth]);

  const fetchTimeline = async () => {
    try {
      const data = await getTimeline();
      setTimeline(data);
    } catch (e) {
      setTimeline([]);
    }
  };

  const fetchAIEvasion = async () => {
    try {
      const data = await getAIEvasion();
      setAIEvasion(data.evasion_level);
    } catch (e) {
      setAIEvasion(1);
    }
  };

  const handlePhishing = async () => {
    const res = await runPhishing('victim@example.com');
    setAttackResult(res);
    fetchTimeline();
    fetchAIEvasion();
  };

  const handleLateral = async () => {
    const res = await runLateral('host1');
    setAttackResult(res);
    fetchTimeline();
    fetchAIEvasion();
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await login(username, password);
      if (res.success) {
        setAuth(true);
      } else {
        setError(res.error || 'Login failed');
      }
    } catch (err) {
      setError('Login failed');
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await register(username, password);
      if (res.success) {
        setAuthMode('login');
        setError('Registration successful. Please log in.');
      } else {
        setError(res.error || 'Registration failed');
      }
    } catch (err) {
      setError('Registration failed');
    }
  };

  const handleLogout = async () => {
    await logout();
    setAuth(false);
    setAttackResult(null);
    setTimeline([]);
  };

  if (!auth) {
    return (
      <div style={{ minHeight: '100vh', background: '#f4f6fa', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <div style={{ ...cardStyle, width: 350 }}>
          <h2 style={{ textAlign: 'center', marginBottom: 24 }}>ASDF Login</h2>
          <form onSubmit={authMode === 'login' ? handleLogin : handleRegister}>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={e => setUsername(e.target.value)}
              style={{ width: '100%', marginBottom: 12, padding: 10, borderRadius: 4, border: '1px solid #ccc' }}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              style={{ width: '100%', marginBottom: 12, padding: 10, borderRadius: 4, border: '1px solid #ccc' }}
            />
            <button type="submit" style={{ ...buttonStyle, width: '100%' }}>
              {authMode === 'login' ? 'Login' : 'Register'}
            </button>
          </form>
          <button onClick={() => setAuthMode(authMode === 'login' ? 'register' : 'login')} style={{ ...buttonAltStyle, width: '100%', marginTop: 10 }}>
            {authMode === 'login' ? 'Need an account? Register' : 'Have an account? Login'}
          </button>
          {error && <div style={{ color: 'red', marginTop: 10, textAlign: 'center' }}>{error}</div>}
        </div>
      </div>
    );
  }

  return (
    <div style={{ minHeight: '100vh', background: '#f4f6fa' }}>
      {/* Header Bar */}
      <div style={{ background: '#1976d2', color: '#fff', padding: '18px 40px', display: 'flex', alignItems: 'center', justifyContent: 'space-between', boxShadow: '0 2px 8px rgba(0,0,0,0.07)' }}>
        <div style={{ fontWeight: 700, fontSize: 22, letterSpacing: 1 }}>Adversarial Simulation & Detection Framework (ASDF)</div>
        <button onClick={handleLogout} style={logoutButtonStyle}>Logout</button>
      </div>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: 30 }}>
        <div style={{ ...cardStyle, width: '100%', maxWidth: 700 }}>
          <p style={{ margin: 0 }}>Welcome! This dashboard will visualize attacks and detections in real time.</p>
          <p style={{ margin: 0 }}><b>Backend status:</b> {backendMsg}</p>
          <div style={{ margin: '20px 0', fontSize: 18 }}>
            <b>AI Evasion Level:</b> <span style={{ fontSize: 28, color: aiEvasion > 1 ? 'orange' : 'green', fontWeight: 700 }}>{aiEvasion}</span>
          </div>
          <div style={{ marginBottom: 16 }}>
            <button onClick={handlePhishing} style={buttonStyle} onMouseOver={e => e.target.style.background='#1565c0'} onMouseOut={e => e.target.style.background='#1976d2'}>Simulate Phishing Attack</button>
            <button onClick={handleLateral} style={buttonAltStyle} onMouseOver={e => e.target.style.background='#388e3c'} onMouseOut={e => e.target.style.background='#43a047'}>Simulate Lateral Movement Attack</button>
          </div>
          {attackResult && (
            <div style={{ ...cardStyle, background: '#e3f2fd', margin: '20px 0', boxShadow: 'none', border: '1px solid #90caf9' }}>
              <h3 style={{ marginTop: 0 }}>Last Attack/Detection/Response Result:</h3>
              <pre style={{ background: '#f5f5f5', padding: 12, borderRadius: 4, fontSize: 14 }}>{JSON.stringify(attackResult, null, 2)}</pre>
            </div>
          )}
        </div>
        <div style={{ ...cardStyle, width: '100%', maxWidth: 900, marginTop: 20 }}>
          <h2 style={{ marginTop: 0 }}>Attack/Detection/Response Timeline</h2>
          <div style={{ overflowX: 'auto' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse', fontSize: 15 }}>
              <thead>
                <tr style={{ background: '#e3eafc' }}>
                  <th style={{ padding: 8, borderBottom: '2px solid #90caf9' }}>Type</th>
                  <th style={{ padding: 8, borderBottom: '2px solid #90caf9' }}>Data</th>
                  <th style={{ padding: 8, borderBottom: '2px solid #90caf9' }}>MITRE Technique</th>
                  <th style={{ padding: 8, borderBottom: '2px solid #90caf9' }}>Timestamp</th>
                </tr>
              </thead>
              <tbody>
                {timeline.map((event, idx) => (
                  <tr
                    key={idx}
                    style={{
                      background: event.type === 'response' ? '#ffe0e0' : event.type === 'detection' ? '#e0f7fa' : '#fff',
                      transition: 'background 0.2s',
                    }}
                  >
                    <td style={{ padding: 8, fontWeight: 600 }}>{event.type.toUpperCase()}</td>
                    <td style={{ padding: 8 }}><pre style={{ margin: 0, fontSize: 13, background: '#f5f5f5', borderRadius: 4, padding: 8 }}>{event.data}</pre></td>
                    <td style={{ padding: 8 }}>{event.mitre_technique || '-'}</td>
                    <td style={{ padding: 8, color: '#555' }}>{event.timestamp}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
