import React, { useEffect, useState } from 'react';
import { ping, runPhishing, runLateral, getTimeline, login, register, logout, getAIEvasion } from './api';

// Added comment to force Docker build

const cardStyle = {
  background: '#fff',
  borderRadius: 8,
  boxShadow: '0 2px 8px rgba(0,0,0,0.07)',
  padding: '24px',
  margin: '20px auto',
  maxWidth: 900, // Increased max width for better timeline display
};

const buttonStyle = {
  background: '#1976d2',
  color: '#fff',
  border: 'none',
  borderRadius: 4,
  padding: '10px 20px',
  margin: '0 8px 0 0',
  cursor: 'pointer',
  fontWeight: 600,
  fontSize: 16,
  transition: 'background 0.2s',
  flexShrink: 0, // Prevent shrinking in flex container
};

const buttonAltStyle = {
  ...buttonStyle,
  background: '#43a047',
};

const logoutButtonStyle = {
  ...buttonStyle,
  background: '#d32f2f',
  float: 'right',
  margin: 0,
};

const inputStyle = {
  width: '100%',
  marginBottom: 12,
  padding: '10px',
  borderRadius: 4,
  border: '1px solid #ccc',
  boxSizing: 'border-box', // Include padding and border in element's total width and height
};

const errorStyle = {
  color: 'red',
  marginTop: 15,
  textAlign: 'center',
  fontSize: 14,
  fontWeight: 500,
};

const preStyle = {
  background: '#f5f5f5',
  padding: 8, // Reduced padding
  borderRadius: 4,
  fontSize: 13, // Reduced font size
  overflowX: 'auto', // Add horizontal scroll for long content
  whiteSpace: 'pre-wrap', // Wrap long lines
  wordBreak: 'break-all', // Break words if necessary
};

const tableHeaderStyle = {
  padding: '12px 8px',
  borderBottom: '2px solid #90caf9',
  textAlign: 'left',
  backgroundColor: '#e3eafc', // Consistent background
};

const tableCellStyle = {
  padding: '12px 8px',
  verticalAlign: 'top',
  borderBottom: '1px solid #eee',
  wordBreak: 'break-word', // Break long words in cells
};

const tableRowStyle = (eventType) => ({
  background: eventType === 'response' ? '#ffebee' : eventType === 'detection' ? '#e0f2f7' : '#fff',
  transition: 'background 0.2s',
  '&:hover': { // Example hover effect
    background: eventType === 'response' ? '#ffdddd' : eventType === 'detection' ? '#ccecfb' : '#f0f0f0',
  }
});

function App() {
  const [backendMsg, setBackendMsg] = useState('');
  const [attackResult, setAttackResult] = useState(null);
  const [timeline, setTimeline] = useState([]);
  const [aiEvasion, setAIEvasion] = useState(1);
  const [auth, setAuth] = useState(false);
  const [authMode, setAuthMode] = useState('login');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    ping().then(data => setBackendMsg(data.message)).catch(err => {
      console.error("Ping failed:", err);
      setBackendMsg('Backend unreachable');
    });
  }, []);

  useEffect(() => {
    if (auth) {
      fetchTimeline();
      fetchAIEvasion();
      // Fetch periodically
      const intervalId = setInterval(() => {
        fetchTimeline();
        fetchAIEvasion();
      }, 10000); // Fetch every 10 seconds
      return () => clearInterval(intervalId); // Cleanup on unmount or auth change
    }
  }, [auth]);

  const fetchTimeline = async () => {
    try {
      const data = await getTimeline();
      console.log("Timeline data received:", data); // Log received data
      // Data is already parsed JSON from backend, no need to stringify here unless needed for display
      setTimeline(data);
    } catch (e) {
      console.error("Failed to fetch timeline:", e);
      // If unauthorized, set auth to false to show login page
      if (e.response && e.response.status === 401) {
         setAuth(false);
      }
      setTimeline([]);
    }
  };

  const fetchAIEvasion = async () => {
    try {
      const data = await getAIEvasion();
      setAIEvasion(data.evasion_level);
    } catch (e) {
      console.error("Failed to fetch AI Evasion:", e);
       // If unauthorized, set auth to false to show login page
      if (e.response && e.response.status === 401) {
         setAuth(false);
      }
      setAIEvasion(1);
    }
  };

  const handlePhishing = async () => {
    setAttackResult(null); // Clear previous result
    try {
      const res = await runPhishing('victim@example.com');
      setAttackResult(res);
      fetchTimeline();
      fetchAIEvasion();
    } catch (err) {
      console.error("Phishing attack failed:", err);
       if (err.response && err.response.status === 401) {
         setAuth(false);
      }
      setAttackResult({ error: "Failed to run phishing attack." });
    }
  };

  const handleLateral = async () => {
     setAttackResult(null); // Clear previous result
    try {
      const res = await runLateral('host1');
      setAttackResult(res);
      fetchTimeline();
      fetchAIEvasion();
    } catch (err) {
      console.error("Lateral movement attack failed:", err);
       if (err.response && err.response.status === 401) {
         setAuth(false);
      }
       setAttackResult({ error: "Failed to run lateral movement attack." });
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    console.log("Attempting login with:", { username, password }); // Log login attempt
    try {
      const res = await login(username, password);
      if (res.success) {
        setAuth(true);
        setError(''); // Clear error on successful login
      } else {
        setError(res.error || 'Login failed');
      }
    } catch (err) {
      console.error("Login error:", err.response?.data || err.message);
      setError('Login failed. Please check credentials and try again.');
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    console.log("Attempting registration with:", { username, password }); // Log registration attempt
    try {
      const res = await register(username, password);
      if (res.success) {
        setAuthMode('login');
        setError('Registration successful. Please log in.');
        setUsername(''); // Clear form
        setPassword('');
      } else {
        setError(res.error || 'Registration failed');
      }
    } catch (err) {
      console.error("Register error:", err.response?.data || err.message);
      setError('Registration failed. Please try again with a different username.');
    }
  };

  const handleLogout = async () => {
    try {
      await logout();
      setAuth(false);
      setAttackResult(null);
      setTimeline([]);
      setAIEvasion(1);
      setUsername('');
      setPassword('');
       setError(''); // Clear error on logout
    } catch (err) {
      console.error("Logout error:", err);
      setError('Logout failed.');
    }
  };

  if (!auth) {
    return (
      <div style={{ minHeight: '100vh', background: '#f4f6fa', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '20px' }}>
        <div style={{ ...cardStyle, width: '100%', maxWidth: 400, margin: 0 }}>
          <h2 style={{ textAlign: 'center', marginBottom: 24, color: '#333' }}>ASDF {authMode === 'login' ? 'Login' : 'Register'}</h2>
          <form onSubmit={authMode === 'login' ? handleLogin : handleRegister}>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={e => setUsername(e.target.value)}
              style={inputStyle}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              style={inputStyle}
              required
            />
            <button type="submit" style={{ ...buttonStyle, width: '100%', margin: '15px 0 0 0' }}>
              {authMode === 'login' ? 'Login' : 'Register'}
            </button>
          </form>
          <button onClick={() => setAuthMode(authMode === 'login' ? 'register' : 'login')} style={{ ...buttonAltStyle, width: '100%', marginTop: 15, background: '#607d8b' }} onMouseOver={e => e.target.style.background='#546e7a'} onMouseOut={e => e.target.style.background='#607d8b'}>
            {authMode === 'login' ? 'Need an account? Register' : 'Have an account? Login'}
          </button>
          {error && <div style={errorStyle}>{error}</div>}
        </div>
      </div>
    );
  }

  return (
    <div style={{ minHeight: '100vh', background: '#f4f6fa', paddingBottom: '40px' }}>
      {/* Header Bar */}
      <div style={{ background: '#1976d2', color: '#fff', padding: '18px 40px', display: 'flex', alignItems: 'center', justifyContent: 'space-between', boxShadow: '0 2px 8px rgba(0,0,0,0.07)' }}>
        <div style={{ fontWeight: 700, fontSize: 22, letterSpacing: 1 }}>Adversarial Simulation & Detection Framework (ASDF)</div>
        <button onClick={handleLogout} style={logoutButtonStyle}>Logout</button>
      </div>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '0 20px' }}>
        <div style={{ ...cardStyle, width: '100%' }}>
          <h2 style={{ marginTop: 0, marginBottom: 16, color: '#333' }}>Dashboard Overview</h2>
          <p style={{ margin: '0 0 8px 0' }}>Welcome! This dashboard visualizes attacks and detections in real time.</p>
          <p style={{ margin: '0 0 16px 0' }}><b>Backend status:</b> {backendMsg}</p>
          <div style={{ marginBottom: 24, fontSize: 18 }}>
            <b>AI Evasion Level:</b> <span style={{ fontSize: 28, color: aiEvasion > 1 ? 'orange' : 'green', fontWeight: 700 }}>{aiEvasion}</span>
          </div>
          <div style={{ marginBottom: 16, display: 'flex', gap: '10px' }}> {/* Added gap for spacing */}
            <button onClick={handlePhishing} style={buttonStyle} onMouseOver={e => e.target.style.background='#1565c0'} onMouseOut={e => e.target.style.background='#1976d2'}>Simulate Phishing Attack</button>
            <button onClick={handleLateral} style={buttonAltStyle} onMouseOver={e => e.target.style.background='#388e3c'} onMouseOut={e => e.target.style.background='#43a047'}>Simulate Lateral Movement Attack</button>
          </div>
          {attackResult && (
            <div style={{ ...cardStyle, background: '#e3f2fd', margin: '20px 0 0 0', boxShadow: 'none', border: '1px solid #90caf9', padding: 16, maxWidth: '100%' }}> {/* Adjusted padding and maxWidth */}
              <h3 style={{ marginTop: 0, color: '#0d47a1' }}>Last Attack/Detection/Response Result:</h3>
              <pre style={preStyle}>{JSON.stringify(attackResult, null, 2)}</pre>
            </div>
          )}
        </div>
        <div style={{ ...cardStyle, width: '100%', marginTop: 20 }}>
          <h2 style={{ marginTop: 0, marginBottom: 16, color: '#333' }}>Attack/Detection/Response Timeline</h2>
          <div style={{ overflowX: 'auto' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse', fontSize: 15 }}>
              <thead>
                <tr style={{ background: '#e3eafc' }}>
                  <th style={tableHeaderStyle}>Type</th>
                  <th style={tableHeaderStyle}>Data</th>
                  <th style={tableHeaderStyle}>MITRE Technique</th>
                  <th style={tableHeaderStyle}>Timestamp</th>
                </tr>
              </thead>
              <tbody>
                {timeline.map((event, idx) => (
                  <tr
                    key={idx}
                    style={tableRowStyle(event.type)} // Use dynamic style function
                  >
                    <td style={{ ...tableCellStyle, fontWeight: 600, color: '#555' }}>{event.type.toUpperCase()}</td>
                    <td style={tableCellStyle}><pre style={preStyle}>{JSON.stringify(event.data, null, 2)}</pre></td>{/* Render stringified data */}
                    <td style={tableCellStyle}>{event.mitre_technique || '-'}</td>
                    <td style={{ ...tableCellStyle, color: '#777' }}>{event.timestamp}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
